mapper.select:
		
		SELECT EVENT_NUM, EVENT_TITLE, EVENT_CONT, START_DATE, END_DATE, WRITE_DATE, EVENT_FILE
		FROM EVENT

		WHERE EVENT_NUM = #{eventNum}

SELECT B.* FROM (SELECT A.*,  ROWNUM RN FROM (
SELECT * FROM EVENT
ORDER BY EVENT_NUM DESC
) A) B WHERE RN BETWEEN #{first} and #{last}

mapper.insert:
SELECT MAX(EVENT_NUM)+1 FROM EVENT
INSERT INTO EVENT ( EVENT_NUM, EVENT_TITLE, EVENT_CONT, START_DATE, END_DATE, WRITE_DATE, MEMBER_ID
,EVENT_FILE
) VALUES ( #{eventNum}, #{eventTitle}, #{eventCont}, #{startDate}, #{endDate}, sysdate, #{memberId}
