mapper.select:

	SELECT ID
	FROM MEMBER
	WHERE NAME = #{name}
	AND PHONE = #{phone}


	SELECT PASSWORD
	FROM MEMBER
	WHERE ID = #{id}
	AND NAME = #{name}
	AND PHONE = #{phone}

mapper.update:
UPDATE MEMBER
PASSWORD = #{password}, NAME = #{name}, PHONE = #{phone}, BIRTH = #{birth}, ADDRESS = #{address}, MEM_SEPARATION = #{memSeparation}, OUT_COUNT = #{outCount},
WHERE ID = #{id}

update member
set exp_date = sysdate + 365
where id = #{id}
