mapper.select:
parameterType="review">
SELECT * FROM BOOK INNER JOIN REVIEW
ON
BOOK.ISBN = REVIEW.ISBN
WHERE REVIEW.ISBN=#{isbn};

WHERE RN <= #{pageUnit}

mapper.update:
UPDATE REVIEW SET
REVIEW_TITLE=#{review_title},
REVIEW_CONT=#{review_cont},
REVIEW_POINT=#{review_point}
WHERE MEMBER_ID=#{member_id} AND ISBN=#{isbn}
